import robot

def RobotControl():
	a = robot.readsensor(0)
	if(a > 16500):
		robot.motors(20000, 0)	# Motor 0 go, Motor 1 stop (turn right)
	else:
		robot.motors(0, 20000)	# Motor 0 stop, Motor 1 go (turn left)

robot.timer(freq = 10, callback = RobotControl)

